import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import api from '../services/api';

const Messages = () => {
  const [searchParams] = useSearchParams();
  const vendeurId = searchParams.get('vendeur');
  const [message, setMessage] = useState('');
  const [sent, setSent] = useState(false);
  const [conversations, setConversations] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Charger la liste des conversations/messages de l'utilisateur connecté
    const fetchConversations = async () => {
      try {
        const response = await api.get('/messaging/conversations/');
        setConversations(response.data);
      } catch {
        setConversations([]);
      } finally {
        setLoading(false);
      }
    };
    fetchConversations();
  }, [sent]); // Rafraîchir la liste après envoi

  const handleSend = async (e) => {
    e.preventDefault();
    if (!vendeurId || !message) return;
    try {
      await api.post('/messaging/messages/send/', {
        recipient_id: vendeurId,
        content: message,
      });
      setSent(true);
      setMessage('');
      setTimeout(() => setSent(false), 2000);
    } catch {
      alert("Erreur lors de l'envoi du message.");
    }
  };

  return (
    <div className="p-4 max-w-xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Messages</h1>
      <p className="mb-4 text-gray-600">Interface de messagerie pour communiquer avec d'autres utilisateurs.</p>
      {vendeurId && (
        <form onSubmit={handleSend} className="mb-6">
          <label className="block mb-2 font-semibold">Envoyer un message à l'utilisateur {vendeurId}</label>
          <textarea
            className="w-full border rounded p-2 mb-2"
            value={message}
            onChange={e => setMessage(e.target.value)}
            placeholder="Votre message..."
            required
          />
          <button
            type="submit"
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
          >
            Envoyer
          </button>
          {sent && (
            <div className="mt-2 text-green-600">Message envoyé !</div>
          )}
        </form>
      )}
      <h2 className="text-lg font-semibold mt-8 mb-2">Mes conversations</h2>
      {loading ? (
        <div>Chargement des conversations...</div>
      ) : conversations.length === 0 ? (
        <p className="text-gray-500">Aucune conversation pour le moment.</p>
      ) : (
        <ul className="divide-y divide-gray-200">
          {conversations.map(conv => (
            <li key={conv.id} className="py-2">
              <span className="font-semibold">{conv.subject || `Conversation #${conv.id}`}</span>
              <div className="text-sm text-gray-600">
                Participants : {conv.participants && conv.participants.join(', ')}
              </div>
              <div className="text-xs text-gray-400">Créée le {new Date(conv.created_at).toLocaleString()}</div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Messages;
