import React, { useEffect, useState } from 'react';
import ListingCard from '../components/ListingCard';
import CategoryFilter from '../components/CategoryFilter';
import SearchBar from '../components/SearchBar';
import { getListings } from '../services/listingService';

const Home = () => {
  const [listings, setListings] = useState([]);
  const [filteredListings, setFilteredListings] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    fetchListings();
  }, []);

  const fetchListings = async () => {
    try {
      const response = await getListings();
      setListings(response.data);
      setFilteredListings(response.data);
    } catch (error) {
      console.error('Erreur lors du chargement des annonces', error);
    }
  };

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    filterListings(category, searchQuery);
  };

  const handleSearch = (query) => {
    setSearchQuery(query);
    filterListings(selectedCategory, query);
  };

  const filterListings = (category, query) => {
    let filtered = listings;
    if (category) {
      filtered = filtered.filter(
        (listing) => listing.category && listing.category.toLowerCase() === category.toLowerCase()
      );
    }
    if (query) {
      filtered = filtered.filter(
        (listing) =>
          listing.title.toLowerCase().includes(query.toLowerCase()) ||
          (listing.description && listing.description.toLowerCase().includes(query.toLowerCase()))
      );
    }
    setFilteredListings(filtered);
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Bienvenue sur Vinted</h1>
      <CategoryFilter selectedCategory={selectedCategory} onSelectCategory={handleCategorySelect} />
      <SearchBar onSearch={handleSearch} />
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
        {filteredListings.length > 0 ? (
          filteredListings.map((listing) => <ListingCard key={listing.id} listing={listing} />)
        ) : (
          <p>Aucune annonce trouv√©e.</p>
        )}
      </div>
    </div>
  );
};

export default Home;
